import React, { useState, useEffect } from "react";
import { storage, auth, db } from "../firebase";
import { ref, uploadBytes, getDownloadURL } from "firebase/storage";
import { doc, setDoc, getDoc } from "firebase/firestore";

export default function Profile() {
  const user = JSON.parse(localStorage.getItem("user"));
  const [profile, setProfile] = useState({
    name: user?.name || "",
    age: "",
    linkedIn: "",
    college: "",
    state: "",
    city: "",
    photoURL: user?.photoURL || user?.profilePhoto || "", // fallback to profilePhoto
    skills: [],
  });

  const [skillInput, setSkillInput] = useState("");

  // Fetch profile from Firestore
  useEffect(() => {
    if (!user?.googleId) return;

    const fetchProfile = async () => {
      try {
        const docRef = doc(db, "users", user.googleId);
        const docSnap = await getDoc(docRef);
        if (docSnap.exists()) {
          const data = docSnap.data();
          setProfile({
            ...profile,
            ...data,
            photoURL: data.photoURL || data.profilePhoto || "",
          });
        }
      } catch (err) {
        console.error("Failed to fetch profile:", err);
      }
    };
    fetchProfile();
    // eslint-disable-next-line
  }, [user?.googleId]);

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleSkillAdd = () => {
    if (skillInput && !profile.skills.includes(skillInput)) {
      setProfile({ ...profile, skills: [...profile.skills, skillInput] });
      setSkillInput("");
    }
  };

  const handleSkillRemove = (skill) => {
    setProfile({
      ...profile,
      skills: profile.skills.filter((s) => s !== skill),
    });
  };

  const handlePhotoUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const storageRef = ref(storage, `profiles/${user.googleId}`);
    await uploadBytes(storageRef, file);
    const url = await getDownloadURL(storageRef);
    // Update profile state
    setProfile((prev) => ({ ...prev, photoURL: url }));

    // Immediately save to Firestore
    await setDoc(doc(db, "users", user.googleId), {
      ...profile,
      photoURL: url,
    });
  };

  const handleSave = async () => {
    if (!user?.googleId) {
      alert("User not found. Please log in again.");
      return;
    }

    try {
    const updatedProfile = { ...user, ...profile };
    await setDoc(doc(db, "users", user.googleId), updatedProfile);
    localStorage.setItem("user", JSON.stringify(updatedProfile));
    alert("Profile saved!");
  } catch (err) {
    console.error("Failed to save profile:", err);
    alert("Failed to save profile. Check console for details.");
  }
  };

  return (
    <div
      style={{ maxWidth: "600px", margin: "50px auto", textAlign: "center" }}
    >
      <h1>Profile</h1>

      <input
        type="text"
        name="name"
        placeholder="Name"
        value={profile.name}
        onChange={handleChange}
        style={{ width: "100%", padding: "8px", margin: "8px 0" }}
      />
      <input
        type="number"
        name="age"
        placeholder="Age"
        value={profile.age}
        onChange={handleChange}
        style={{ width: "100%", padding: "8px", margin: "8px 0" }}
      />
      <input
        type="text"
        name="college"
        placeholder="College"
        value={profile.college}
        onChange={handleChange}
        style={{ width: "100%", padding: "8px", margin: "8px 0" }}
      />
      <input
        type="text"
        name="state"
        placeholder="State"
        value={profile.state}
        onChange={handleChange}
        style={{ width: "100%", padding: "8px", margin: "8px 0" }}
      />
      <input
        type="text"
        name="city"
        placeholder="City"
        value={profile.city}
        onChange={handleChange}
        style={{ width: "100%", padding: "8px", margin: "8px 0" }}
      />
      <input
        type="text"
        name="linkedIn"
        placeholder="LinkedIn URL"
        value={profile.linkedIn}
        onChange={handleChange}
        style={{ width: "100%", padding: "8px", margin: "8px 0" }}
      />

      {/* Skills */}
      <div style={{ margin: "10px 0" }}>
        <input
          type="text"
          placeholder="Add skill"
          value={skillInput}
          onChange={(e) => setSkillInput(e.target.value)}
          style={{ padding: "8px", width: "70%" }}
        />
        <button
          onClick={handleSkillAdd}
          style={{ padding: "8px 12px", marginLeft: "10px" }}
        >
          Add
        </button>
      </div>

      <div style={{ marginBottom: "10px" }}>
        {profile.skills.map((skill) => (
          <span
            key={skill}
            style={{
              display: "inline-block",
              background: "#ddd",
              padding: "5px 10px",
              borderRadius: "15px",
              margin: "5px",
              cursor: "pointer",
            }}
            onClick={() => handleSkillRemove(skill)}
          >
            {skill} ×
          </span>
        ))}
      </div>

      {/* Profile Photo */}
      <div style={{ margin: "10px 0" }}>
        <input type="file" onChange={handlePhotoUpload} />
        {profile.photoURL && (
          <img
            src={profile.photoURL}
            alt="Profile"
            style={{ width: "100px", borderRadius: "50%", marginTop: "10px" }}
          />
        )}
      </div>

      <button
        onClick={handleSave}
        style={{
          padding: "10px 20px",
          background: "#4CAF50",
          color: "#fff",
          border: "none",
          borderRadius: "8px",
          cursor: "pointer",
        }}
      >
        Save Profile
      </button>
    </div>
  );
}
