import React, { useState, useEffect } from "react";
import { auth, db } from "../firebase";
import { collection, getDocs, addDoc, serverTimestamp } from "firebase/firestore";

export default function Home() {
  const user = JSON.parse(localStorage.getItem("user"));
  const [users, setUsers] = useState([]);

  // Fetch all users from Firestore
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, "users"));
        const allUsers = [];
        querySnapshot.forEach((doc) => {
          allUsers.push(doc.data());
        });
        setUsers(allUsers);
      } catch (err) {
        console.error("Error fetching users:", err);
      }
    };
    fetchUsers();
  }, []);

  // Send connection request
  const sendRequest = async (toUser) => {
    try {
      await addDoc(collection(db, "requests"), {
        fromId: user.googleId,
        toId: toUser.googleId,
        status: "pending",
        timestamp: serverTimestamp(),
      });
      alert(`Request sent to ${toUser.name}`);
    } catch (err) {
      console.error("Error sending request:", err);
    }
  };

  return (
    <div
      style={{
        padding: "20px",
        background: "#f5f1e8", // soft beige background
        minHeight: "100vh",
        color: "#333",
      }}
    >
      {/* Welcome Section */}
      <div style={{ textAlign: "center", margin: "20px 0" }}>
        <h1 style={{ color: "#b5895b" }}>👋 Welcome, {user?.name || "Guest"}</h1>
        {!user && <p style={{ color: "#666" }}>Log in to see other users.</p>}
      </div>

      {/* Users Grid */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
          gap: "20px",
        }}
      >
        {users
          .filter((u) => u.googleId !== user?.googleId) // exclude current user
          .map((u) => (
            <div
              key={u.googleId}
              style={{
                borderRadius: "12px",
                padding: "15px",
                textAlign: "center",
                background: "#fff8f0",
                boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
              }}
            >
              {/* Profile Photo */}
              {u.photoURL && (
                <img
                  src={u.photoURL}
                  alt={u.name}
                  style={{
                    width: "80px",
                    height: "80px",
                    borderRadius: "50%",
                    marginBottom: "10px",
                    border: "2px solid #b5895b",
                  }}
                />
              )}

              {/* Name */}
              <h3 style={{ margin: "6px 0", color: "#333" }}>{u.name}</h3>

              {/* Age */}
              {u.age && (
                <p style={{ color: "#666", margin: "4px 0" }}>{u.age}</p>
              )}

              {/* Profession */}
              {u.profession && (
                <p style={{ fontWeight: "bold", color: "#b5895b", margin: "2px 0" }}>
                  {u.profession}
                </p>
              )}

              {/* Location */}
              {(u.city || u.state) && (
                <p style={{ color: "#555", margin: "2px 0" }}>
                  📍 {u.city && `${u.city}, `}{u.state}
                </p>
              )}

              {/* LinkedIn */}
              {u.linkedIn && (
                <p style={{ margin: "6px 0" }}>
                  <a
                    href={u.linkedIn}
                    target="_blank"
                    rel="noopener noreferrer"
                    style={{
                      color: "#0A66C2",
                      textDecoration: "underline",
                      fontWeight: "500",
                    }}
                  >
                    LinkedIn Profile
                  </a>
                </p>
              )}

              {/* Skills */}
              {u.skills?.length > 0 && (
                <div
                  style={{
                    display: "flex",
                    flexWrap: "wrap",
                    justifyContent: "center",
                    marginTop: "10px",
                  }}
                >
                  {u.skills.map((skill) => (
                    <span
                      key={skill}
                      style={{
                        background: "#b5895b22",
                        borderRadius: "12px",
                        padding: "4px 10px",
                        margin: "4px",
                        fontSize: "12px",
                        color: "#333",
                        fontWeight: "500",
                      }}
                    >
                      {skill}
                    </span>
                  ))}
                </div>
              )}

              {/* Buttons */}
              <div style={{ marginTop: "15px", display: "flex", gap: "10px" }}>
                {/* Send Request */}
                <button
                  style={{
                    padding: "8px 14px",
                    borderRadius: "6px",
                    cursor: "pointer",
                    background: "#ff4d4d",
                    color: "#fff",
                    border: "none",
                    fontWeight: "500",
                    transition: "background 0.2s",
                  }}
                  onClick={() => sendRequest(u)}
                  onMouseEnter={(e) => (e.currentTarget.style.background = "#e04343")}
                  onMouseLeave={(e) => (e.currentTarget.style.background = "#ff4d4d")}
                >
                  Send Request
                </button>

                {/* Calendly Button (only if available) */}
                {u.calendlyLink && (
                  <button
                    style={{
                      padding: "8px 14px",
                      borderRadius: "6px",
                      cursor: "pointer",
                      background: "#b5895b",
                      color: "#fff",
                      border: "none",
                      fontWeight: "500",
                      transition: "background 0.2s",
                    }}
                    onClick={() => window.open(u.calendlyLink, "_blank")}
                    onMouseEnter={(e) => (e.currentTarget.style.background = "#a1794e")}
                    onMouseLeave={(e) => (e.currentTarget.style.background = "#b5895b")}
                  >
                    Book Meeting
                  </button>
                )}
              </div>
            </div>
          ))}
      </div>
    </div>
  );
}
