import React from "react";
import { Link } from "react-router-dom";
import { signOut } from "firebase/auth";
import { auth } from "../firebase";

export default function Navbar() {
  const handleLogout = () => {
    signOut(auth);
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    window.location.href = "/login";
  };

  const user = JSON.parse(localStorage.getItem("user"));

  return (
    <nav
      style={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        padding: "12px 25px",
        background: "#4169E1", // Royal Blue
        color: "white",
        boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
        borderBottom: "3px solid #D6E6FF", // Light Blue accent
        position: "sticky",
        top: 0,
        zIndex: 1000,
      }}
    >
      {/* Logo */}
      <h2 style={{ fontSize: "22px", fontWeight: "bold", letterSpacing: "1px" }}>
        SkillSync
      </h2>

      {/* Navigation Links */}
      {user && (
        <div style={{ display: "flex", gap: "20px", alignItems: "center" }}>
          <Link
            to="/"
            style={{
              color: "white",
              textDecoration: "none",
              fontWeight: "500",
              transition: "color 0.2s",
            }}
            onMouseEnter={(e) => (e.target.style.color = "#D6E6FF")}
            onMouseLeave={(e) => (e.target.style.color = "white")}
          >
            Home
          </Link>

          <Link
            to="/messages"
            style={{
              color: "white",
              textDecoration: "none",
              fontWeight: "500",
              transition: "color 0.2s",
            }}
            onMouseEnter={(e) => (e.target.style.color = "#D6E6FF")}
            onMouseLeave={(e) => (e.target.style.color = "white")}
          >
            Messages
          </Link>

          <Link
            to="/profile"
            style={{
              color: "white",
              textDecoration: "none",
              fontWeight: "500",
              transition: "color 0.2s",
            }}
            onMouseEnter={(e) => (e.target.style.color = "#D6E6FF")}
            onMouseLeave={(e) => (e.target.style.color = "white")}
          >
            Profile
          </Link>

          {/* Logout Button */}
          <button
            onClick={handleLogout}
            style={{
              padding: "6px 14px",
              borderRadius: "8px",
              border: "none",
              background: "#f44336", // Red for contrast
              color: "white",
              fontWeight: "500",
              cursor: "pointer",
              transition: "background 0.2s",
            }}
            onMouseEnter={(e) => (e.target.style.background = "#d32f2f")}
            onMouseLeave={(e) => (e.target.style.background = "#f44336")}
          >
            Logout
          </button>
        </div>
      )}
    </nav>
  );
}
