import React, { useState, useEffect } from "react";
import { db } from "../firebase";
import {
  collection,
  query,
  where,
  getDocs,
  updateDoc,
  doc,
  orderBy,
  addDoc,
  onSnapshot,
  serverTimestamp
} from "firebase/firestore";

export default function Messages() {
  const user = JSON.parse(localStorage.getItem("user"));
  const [requests, setRequests] = useState([]);
  const [acceptedUsers, setAcceptedUsers] = useState([]); // users you can chat with
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");

  // Fetch incoming requests
  useEffect(() => {
    const q = query(collection(db, "requests"), where("toId", "==", user.googleId));
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const reqs = snapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
      setRequests(reqs.filter(r => r.status === "pending"));
    });
    return () => unsubscribe();
  }, []);

  // Fetch accepted chats
  useEffect(() => {
    const q = query(collection(db, "requests"), where("status", "==", "accepted"));
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const accepted = snapshot.docs
        .map(doc => doc.data())
        .filter(r => r.fromId === user.googleId || r.toId === user.googleId)
        .map(r => (r.fromId === user.googleId ? r.toId : r.fromId));
      setAcceptedUsers(accepted);
    });
    return () => unsubscribe();
  }, []);

  // Fetch messages for selected user
  useEffect(() => {
    if (!selectedUser) return;
    const chatId = [user.googleId, selectedUser].sort().join("_");
    const q = query(collection(db, "messages"), where("chatId", "==", chatId), orderBy("timestamp"));
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const msgs = snapshot.docs.map(doc => doc.data());
      setMessages(msgs);
    });
    return () => unsubscribe();
  }, [selectedUser]);

  const acceptRequest = async (req) => {
    const requestRef = doc(db, "requests", req.id);
    await updateDoc(requestRef, { status: "accepted" });
  };

  const rejectRequest = async (req) => {
    const requestRef = doc(db, "requests", req.id);
    await updateDoc(requestRef, { status: "rejected" });
  };

  const sendMessage = async () => {
    if (!newMessage.trim()) return;
    const chatId = [user.googleId, selectedUser].sort().join("_");
    await addDoc(collection(db, "messages"), {
      chatId,
      fromId: user.googleId,
      toId: selectedUser,
      message: newMessage,
      timestamp: serverTimestamp(),
    });
    setNewMessage("");
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Messages & Requests</h1>

      {/* Incoming Requests */}
      <h2>Incoming Requests</h2>
      {requests.length === 0 && <p>No requests</p>}
      {requests.map((req) => (
        <div key={req.id} style={{ border: "1px solid #ddd", padding: "10px", margin: "10px 0" }}>
          <p>Request from: {req.fromId}</p>
          <button onClick={() => acceptRequest(req)}>Accept</button>
          <button onClick={() => rejectRequest(req)}>Reject</button>
        </div>
      ))}

      {/* Chat List */}
      <h2>Chats</h2>
      {acceptedUsers.length === 0 && <p>No chats yet</p>}
      <div style={{ display: "flex", gap: "10px", marginBottom: "20px" }}>
        {acceptedUsers.map((uId) => (
          <button key={uId} onClick={() => setSelectedUser(uId)}>
            {uId}
          </button>
        ))}
      </div>

      {/* Chat Window */}
      {selectedUser && (
        <div>
          <h3>Chat with {selectedUser}</h3>
          <div
            style={{
              border: "1px solid #ddd",
              padding: "10px",
              height: "300px",
              overflowY: "scroll",
              marginBottom: "10px",
            }}
          >
            {messages.map((msg, idx) => (
              <div
                key={idx}
                style={{
                  textAlign: msg.fromId === user.googleId ? "right" : "left",
                  margin: "5px 0",
                }}
              >
                <span
                  style={{
                    display: "inline-block",
                    padding: "6px 10px",
                    borderRadius: "12px",
                    background: msg.fromId === user.googleId ? "#4CAF50" : "#ddd",
                    color: msg.fromId === user.googleId ? "#fff" : "#000",
                  }}
                >
                  {msg.message}
                </span>
              </div>
            ))}
          </div>
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Type a message..."
            style={{ padding: "8px", width: "80%", marginRight: "10px" }}
          />
          <button onClick={sendMessage} style={{ padding: "8px" }}>
            Send
          </button>
        </div>
      )}
    </div>
  );
}
