import React, { useState, useEffect, useRef } from "react";
import { db, auth } from "../firebase";
import {
  collection,
  addDoc,
  query,
  orderBy,
  onSnapshot,
  serverTimestamp,
} from "firebase/firestore";
import toast from "react-hot-toast";

export default function Messages({ chatId, currentUser, recipient }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const messagesEndRef = useRef(null);

  // ðŸ§  Fetch messages in real time
  useEffect(() => {
    if (!chatId) return;

    const messagesRef = collection(db, "chats", chatId, "messages");
    const q = query(messagesRef, orderBy("createdAt", "asc"));

    const unsubscribe = onSnapshot(q, (snapshot) => {
      const msgs = snapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
      setMessages(msgs);

      // Trigger notification for new incoming message
      const lastMsg = msgs[msgs.length - 1];
      if (lastMsg && lastMsg.senderId !== currentUser.uid) {
        toast(`ðŸ’¬ New message from ${recipient?.name || "a user"}`, {
          icon: "ðŸ””",
          duration: 4000,
        });
      }
    });

    return () => unsubscribe();
  }, [chatId, currentUser.uid, recipient?.name]);

  // ðŸª„ Auto scroll to bottom on new message
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // âœ‰ï¸ Send message
  const sendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    try {
      const msgData = {
        text: newMessage,
        senderId: currentUser.uid,
        senderName: currentUser.displayName,
        createdAt: serverTimestamp(),
      };

      await addDoc(collection(db, "chats", chatId, "messages"), msgData);
      setNewMessage("");

      toast("ðŸ“¤ Message sent!", {
        icon: "âœ…",
        duration: 2000,
      });
    } catch (err) {
      console.error("Failed to send message:", err);
      toast.error("Failed to send message. Try again.");
    }
  };

  // âš¡ Show notification when chat is opened
  useEffect(() => {
    if (recipient?.name)
      toast(`ðŸ’¬ Chat opened with ${recipient.name}`, {
        icon: "ðŸ“¨",
        duration: 2000,
      });
  }, [recipient?.name]);

  return (
    <div
      style={{
        width: "100%",
        maxWidth: "600px",
        margin: "0 auto",
        padding: "20px",
        borderRadius: "10px",
        background: "#f5f7fa",
        boxShadow: "0 2px 12px rgba(0,0,0,0.1)",
        display: "flex",
        flexDirection: "column",
        height: "80vh",
      }}
    >
      <h2 style={{ textAlign: "center", marginBottom: "10px" }}>
        Chat with {recipient?.name || "User"}
      </h2>

      {/* Messages Display */}
      <div
        style={{
          flexGrow: 1,
          overflowY: "auto",
          padding: "10px",
          borderRadius: "8px",
          background: "#fff",
        }}
      >
        {messages.map((msg) => (
          <div
            key={msg.id}
            style={{
              marginBottom: "10px",
              display: "flex",
              justifyContent:
                msg.senderId === currentUser.uid ? "flex-end" : "flex-start",
            }}
          >
            <div
              style={{
                background:
                  msg.senderId === currentUser.uid ? "#4CAF50" : "#e0e0e0",
                color: msg.senderId === currentUser.uid ? "#fff" : "#000",
                padding: "10px 14px",
                borderRadius: "15px",
                maxWidth: "70%",
                wordWrap: "break-word",
              }}
            >
              <span>{msg.text}</span>
            </div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* Message Input */}
      <form
        onSubmit={sendMessage}
        style={{
          display: "flex",
          marginTop: "10px",
          gap: "10px",
        }}
      >
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Type a message..."
          style={{
            flex: 1,
            padding: "10px",
            borderRadius: "8px",
            border: "1px solid #ccc",
          }}
        />
        <button
          type="submit"
          style={{
            padding: "10px 16px",
            border: "none",
            borderRadius: "8px",
            background: "#4CAF50",
            color: "#fff",
            cursor: "pointer",
          }}
        >
          Send
        </button>
      </form>
    </div>
  );
}
